import React, { useState, useEffect } from "react";

const ExamPage = () => {
  const [timeLeft, setTimeLeft] = useState(60); // 60 seconds for the exam
  const [isActive, setIsActive] = useState(true);
  const [answers, setAnswers] = useState([]);

  useEffect(() => {
    let interval = null;
    if (timeLeft === 0) {
      setIsActive(false);
    } else if (isActive) {
      interval = setInterval(() => {
        setTimeLeft(timeLeft - 1);
      }, 500);
    }
    return () => clearInterval(interval);
  }, [timeLeft, isActive]);

  const handleFinishExam = (event) => {
    event.preventDefault();
    submitAnswers();
  };

  const handleAnswerChange = (event, index) => {
    const newAnswers = [...answers];
    newAnswers[index] = event.target.value;
    setAnswers(newAnswers);
  };

  const renderAnswerInputs = () => {
    const inputs = [];
    for (let i = 0; i < 10; i++) {
      // 10 questions in the exam
      inputs.push(
        <div key={i}>
          <label htmlFor={`answer${i}`}>Question {i + 1}:</label>
          <input
            type="text"
            id={`answer${i}`}
            value={answers[i] || ""}
            onChange={(event) => handleAnswerChange(event, i)}
            disabled={!isActive}
          />
        </div>
      );
    }
    return inputs;
  };

  const submitAnswers = () => {
    // TODO: submit the answers to the server
    // fetch("/students", {
    //   method: "POST",
    //   headers: {
    //     "Content-Type": "application/json",
    //     // Authorization: `Bearer ${token}`
    //   },
    //   body: JSON.stringify({
    //     student_id: student,
    //     course_id: course,
    //     message: messages,
    //   }),
    // })
    //   .then((res) => res.json())
    //   .then((response) => console.log(response));
    // console.log("Answers submitted:", answers);
  };

  return (
    <div className="mt-5 ml-10">
      <h1>Exam</h1>
      <p>Time Left: {timeLeft} seconds</p>
      <form onSubmit={handleFinishExam}>
        {renderAnswerInputs()}
        {isActive ? (
          <button type="submit">Finish Exam</button>
        ) : (
          <div>
            {/* {renderAnswerInputs()} */}
            <button type="submit" onClick={submitAnswers}>
              Submit Answers
            </button>
          </div>
        )}
      </form>
    </div>
  );
};

export default ExamPage;
